var app={dial:$(".dial"),site:$("html"),bg:$(".color-bg"),init:function(){app.setBgCheckerColors(203,100,50);app.setMoveEvents()},setBgCheckerColors:function(e,t,n){app.bg.css("background-color","hsla("+e+", "+t+"%, "+n+"%, 1)")},setMoveEvents:function(){app.site.on("mousedown",function(e){app.enableMovement(e)});app.site.on("mouseup",function(e){app.disableMovement(e)})},enableMovement:function(e){TweenMax.to(app.dial,.2,{left:e.pageX,top:e.pageY});app.site.on("mousemove",function(e){app.onMove(e)})},disableMovement:function(e){app.site.off("mousemove")},onMove:function(e){var t=e.pageX>app.dial.width()/2?e.pageX:app.dial.width()/2,n=e.pageY>app.dial.height()/2?e.pageY:app.dial.height()/2;t>app.site.width()-app.dial.width()/2&&(t=app.site.width()-app.dial.width()/2);n>app.bg.height()-app.dial.height()/2&&(n=app.bg.height()-app.dial.height()/2);TweenMax.to(app.dial,.1,{left:t,top:n})}};$(app.init());